import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'Themes';
  showMenu = false;

  toggleMenu() {
    this.showMenu = !this.showMenu;
  }

  box = document.getElementById('iconButton') as HTMLElement;

  if(showMenu = true) {
    this.box.style.setProperty('background-color', 'green')
  }

}
